package model

// OLMMetadata contains all of the metadata that build out a ClusterServiceVersion,
// which represents a given version of a service controller.
type OLMMetadata struct {
	ProjectLongDescription string       `json:"projectLongDescription"`
	DisplayName            string       `json:"displayName"`
	Keywords               []string     `json:"keywords"`
	Links                  []Link       `json:"links"`
	Maintainers            []Maintainer `json:"maintainers"`
	Maturity               string       `json:"maturity"`
	Samples                []Sample     `json:"samples"`
	Icon                   Icon         `json:"icon"`
	Provider               Provider     `json:"provider"`
	Annotations            Annotations  `json:"annotations"`
	InstallModes           InstallModes `json:"installModes"`
}

// Capability is the controller capability level as defined by
// the operator-framework capability model.
type Capability = string

var (
	CapBasicInstall     = "basic install"
	CapSeamlessUpgrades = "seamless upgrades"
	CapFullLifecucle    = "full lifecycle"
	CapDeepInsights     = "deep insights"
	CapAutoPilot        = "auto pilot"
)

// Maturity corresponds with the CSV definition
// https://github.com/operator-framework/operator-lifecycle-manager/blob/master/deploy/upstream/manifests/0.6.0/03-clusterserviceversion.crd.yaml#L138
type Maturity = string

var (
	MatPlanning   Maturity = "planning"
	MatPrealpha   Maturity = "pre-alpha"
	MatAlpha      Maturity = "alpha"
	MatBeta       Maturity = "beta"
	MatStable     Maturity = "stable"
	MatMature     Maturity = "mature"
	MatInactive   Maturity = "inactive"
	MatDeprecated Maturity = "deprecated"
)

// Link represents a link to information that a consumer of a service
// controller may want to browse to find out more about the service controller.
type Link struct {
	Name string `json:"name`
	URL  string `json:"URL"`
}

// Maintainer represents the maintainers of the service controller.
type Maintainer struct {
	Email string `json:"email"`
	Name  string `json:"name"`
}

// Icon represents the icon image used to represent the service controller
// via the OLM graphical user interface.
type Icon struct {
	Base64Data string    `json:"Base64Data"`
	MediaType  Mediatype `json:"mediaType"`
}

// Mediatype represents the various supported mediatypes for the OLM icon.
type Mediatype = string

var (
	MediatypeGIF Mediatype = "image/gif"
	MediatypeJPG Mediatype = "image/jpeg"
	MediatypePNG Mediatype = "image/png"
	MediatypeSVG Mediatype = "image/svg+xml"
)

// Provider represents the overall provider of the service controller.
type Provider struct {
	Name string `json:"name"`
	URL  string `json:"url"`
}

// Annotations represent metadata that is added to the ClusterServiceVersion
// yaml. These keys inform the OLM graphical user interface.
type Annotations struct {
	CapabilityLevel    Capability `json:"capabilityLevel"`
	SuggestedNamespace string     `json:"suggestedNamespace"`
	Repository         string     `json:"repository"`
	ContainerImage     string     `json:"containerImage"`
	ShortDescription   string     `json:"shortDescription"`
	Support            string     `json:"support"`
	IsCertified        bool       `json:"isCertified"`
}

// InstallModes represents the various install modes as defined by OLM.
type InstallModes struct {
	OwnNamespaceSupported    bool `json:"ownNamespaceSupported"`
	SingleNamespaceSupported bool `json:"singleNamespaceSupported"`
	MultiNamespaceSupported  bool `json:"multiNamespaceSupported"`
	AllNamespacesSupported   bool `json:"allNamespacesSupported"`
}

// Sample is a basic representation of an instance of custom resource
// managed by a given service controller.
type Sample struct {
	Kind        string `json:"kind"`
	Spec        string `json:"spec"`
	DisplayName string `json:"displayName"`
	Description string `json:"description"`
}

// OLMCommonMetadata represents common metadata for all service controllers
// generated by this project.
type OLMCommonMetadata struct {
	Links    []Link   `json:"links"`
	Keywords []string `json:"keywords"`
}

var CommonLinks = []Link{
	{
		Name: "AWS Controllers for Kubernetes",
		URL:  "https://github.com/aws-controllers-k8s/community",
	},
	{
		Name: "Documentation",
		URL:  "https://aws-controllers-k8s.github.io/community/",
	},
}

var CommonKeywords = []string{
	"aws",
	"amazon",
	"ack",
}

// NewOLMMetadata returns a default representation of OLMMetadata to be
// used as a base. The various values are expected to be overridden (if
// needed) by an input YAML manifest for the given service controller.
func NewOLMMetadata() OLMMetadata {
	return OLMMetadata{
		Maturity: MatAlpha,
		Icon: Icon{
			// This is the AWS logo
			Base64Data: "",
			MediaType:  MediatypeSVG,
		},
		Provider: Provider{
			Name: "Amazon, Inc.",
			URL:  "https://aws.amazon.com",
		},
		Annotations: Annotations{
			Repository:         "https://github.com/aws-controllers-k8s",
			SuggestedNamespace: "ack-system",
			ShortDescription:   "This is the placeholder short description for the default configuration",
			IsCertified:        false,
			ContainerImage:     "public.ecr.aws/aws-controllers-k8s/controller",
			Support:            "Community",
		},
		InstallModes: InstallModes{
			AllNamespacesSupported: true,
		},
	}
}
